// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title JackpotManager
 * @dev Manages the SuperJackpot Pool with ABC surplus rules and Merkle claims
 *
 * Architecture:
 * - Receives pass funds from LaBolitaVRF (1 USDT per pass)
 * - 5% fee to treasury, 95% to SuperJackpotPool
 * - ABC Rules for surplus distribution
 * - Merkle tree claims for winners
 * - Never touches user funds directly (LaBolitaVRF handles escrow)
 *
 * ABC Rules (order B -> A -> C):
 * - Rule B: If NO 4D base winners, surplus 4D -> jackpot (5% fee)
 * - Rule A: If NO 2D+3D+4D base winners, remaining surplus -> jackpot (5% fee)
 * - Rule C: Daily 5% payout from pool ONLY if pass winners exist
 */
contract JackpotManager is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============

    IERC20 public paymentToken;
    address public laBolitaVRF;              // Only LaBolitaVRF can register passes
    address public treasury;                  // Fee recipient

    // Pool tracking
    uint256 public superJackpotPool;          // Main jackpot pool
    uint256 public pendingPassFunds;          // Funds pending distribution
    uint256 public totalPassesRegistered;     // Total passes sold
    uint256 public totalClaimsPaid;           // Total claims paid out

    // Fee configuration (in basis points)
    uint256 public passFeePercent = 500;      // 5% fee on pass purchases
    uint256 public surplusFeePercent = 500;   // 5% fee on surplus contributions
    uint256 public dailyPayoutPercent = 500;  // 5% daily payout to winners (Rule C)

    // Round management
    uint256 public currentRoundId;
    mapping(uint256 => Round) public rounds;

    struct Round {
        uint256 id;
        uint256 startTime;
        uint256 endTime;
        uint256 totalPasses;           // Total passes in this round
        uint256 totalPassFunds;        // Total pass funds collected
        uint256 surplusContributed;    // Surplus from ABC rules
        uint256 dailyPayout;           // Amount to distribute (Rule C)
        bytes32 merkleRoot;            // Merkle root for claims
        bool settled;                  // Round settlement status
        bool claimsOpen;               // Claims enabled
    }

    // Pass tracking per ticket
    mapping(uint256 => PassInfo) public ticketPasses;

    struct PassInfo {
        address owner;
        uint256 roundId;
        uint256 purchaseTime;
        bool hasClaimed;
    }

    // Merkle claims tracking
    mapping(uint256 => mapping(address => bool)) public roundClaimed;

    // ============ Events ============

    event PassRegistered(uint256 indexed ticketId, address indexed owner, uint256 roundId, uint256 amount);
    event PassFeeCollected(uint256 amount, address treasury);
    event PoolFunded(uint256 amount, uint256 newTotal);
    event SurplusContributed(uint256 roundId, uint256 amount, string ruleType);
    event RoundCreated(uint256 indexed roundId, uint256 startTime);
    event RoundSettled(uint256 indexed roundId, bytes32 merkleRoot, uint256 dailyPayout);
    event ClaimProcessed(uint256 indexed roundId, address indexed user, uint256 amount);
    event TreasuryUpdated(address newTreasury);
    event LaBolitaVRFUpdated(address newLaBolitaVRF);

    // ============ Modifiers ============

    modifier onlyLaBolitaVRF() {
        require(msg.sender == laBolitaVRF, "Only LaBolitaVRF");
        _;
    }

    modifier roundExists(uint256 _roundId) {
        require(_roundId > 0 && _roundId <= currentRoundId, "Round does not exist");
        _;
    }

    // ============ Constructor ============

    constructor(
        address _paymentToken,
        address _treasury
    ) Ownable(msg.sender) {
        paymentToken = IERC20(_paymentToken);
        treasury = _treasury;

        // Create first round
        currentRoundId = 1;
        rounds[currentRoundId] = Round({
            id: currentRoundId,
            startTime: block.timestamp,
            endTime: 0,
            totalPasses: 0,
            totalPassFunds: 0,
            surplusContributed: 0,
            dailyPayout: 0,
            merkleRoot: bytes32(0),
            settled: false,
            claimsOpen: false
        });

        emit RoundCreated(currentRoundId, block.timestamp);
    }

    // ============ LaBolitaVRF Integration ============

    /**
     * @dev Called by LaBolitaVRF after batch checkout with passes
     * Receives USDT and registers which tickets have passes
     * @param _ticketIds Array of ticket IDs that purchased passes
     * @param _totalPassAmount Total USDT received for passes
     */
    function registerPassTickets(
        uint256[] calldata _ticketIds,
        uint256 _totalPassAmount
    ) external onlyLaBolitaVRF nonReentrant whenNotPaused {
        require(_ticketIds.length > 0, "No tickets");
        require(_totalPassAmount > 0, "No pass amount");

        // Calculate fee (5%)
        uint256 fee = (_totalPassAmount * passFeePercent) / 10000;
        uint256 toPool = _totalPassAmount - fee;

        // Transfer fee to treasury
        if (fee > 0) {
            paymentToken.safeTransfer(treasury, fee);
            emit PassFeeCollected(fee, treasury);
        }

        // Add to pool
        superJackpotPool += toPool;
        emit PoolFunded(toPool, superJackpotPool);

        // Register each ticket's pass
        Round storage round = rounds[currentRoundId];
        uint256 passPrice = _totalPassAmount / _ticketIds.length;

        for (uint i = 0; i < _ticketIds.length; i++) {
            uint256 ticketId = _ticketIds[i];
            require(ticketPasses[ticketId].owner == address(0), "Pass already registered");

            ticketPasses[ticketId] = PassInfo({
                owner: tx.origin,  // Original user (through LaBolitaVRF)
                roundId: currentRoundId,
                purchaseTime: block.timestamp,
                hasClaimed: false
            });

            emit PassRegistered(ticketId, tx.origin, currentRoundId, passPrice);
        }

        // Update round stats
        round.totalPasses += _ticketIds.length;
        round.totalPassFunds += toPool;
        totalPassesRegistered += _ticketIds.length;
    }

    // ============ ABC Surplus Rules ============

    /**
     * @dev Rule B: No 4D (Parle) winners - surplus goes to jackpot
     * Called by admin/scheduler after draw results
     * @param _roundId Round to contribute to
     * @param _surplusAmount 4D surplus amount
     */
    function contributeSurplusRuleB(
        uint256 _roundId,
        uint256 _surplusAmount
    ) external onlyOwner roundExists(_roundId) nonReentrant {
        require(_surplusAmount > 0, "No surplus");

        // Transfer surplus from caller (admin/scheduler must approve)
        paymentToken.safeTransferFrom(msg.sender, address(this), _surplusAmount);

        // 5% fee
        uint256 fee = (_surplusAmount * surplusFeePercent) / 10000;
        uint256 toPool = _surplusAmount - fee;

        if (fee > 0) {
            paymentToken.safeTransfer(treasury, fee);
        }

        superJackpotPool += toPool;
        rounds[_roundId].surplusContributed += toPool;

        emit SurplusContributed(_roundId, toPool, "B_NO_4D_WINNERS");
        emit PoolFunded(toPool, superJackpotPool);
    }

    /**
     * @dev Rule A: No winners at all (2D+3D+4D) - remaining surplus to jackpot
     * @param _roundId Round to contribute to
     * @param _surplusAmount Remaining surplus amount
     */
    function contributeSurplusRuleA(
        uint256 _roundId,
        uint256 _surplusAmount
    ) external onlyOwner roundExists(_roundId) nonReentrant {
        require(_surplusAmount > 0, "No surplus");

        paymentToken.safeTransferFrom(msg.sender, address(this), _surplusAmount);

        uint256 fee = (_surplusAmount * surplusFeePercent) / 10000;
        uint256 toPool = _surplusAmount - fee;

        if (fee > 0) {
            paymentToken.safeTransfer(treasury, fee);
        }

        superJackpotPool += toPool;
        rounds[_roundId].surplusContributed += toPool;

        emit SurplusContributed(_roundId, toPool, "A_NO_WINNERS");
        emit PoolFunded(toPool, superJackpotPool);
    }

    // ============ Round Settlement ============

    /**
     * @dev Settle round and set Merkle root for claims
     * Rule C: 5% daily payout IF pass winners exist
     * @param _roundId Round to settle
     * @param _merkleRoot Merkle root of eligible claimers
     * @param _hasPassWinners Whether any pass holders won
     */
    function settleRound(
        uint256 _roundId,
        bytes32 _merkleRoot,
        bool _hasPassWinners
    ) external onlyOwner roundExists(_roundId) {
        Round storage round = rounds[_roundId];
        require(!round.settled, "Already settled");

        round.endTime = block.timestamp;
        round.merkleRoot = _merkleRoot;
        round.settled = true;

        // Rule C: Only distribute if pass winners exist
        if (_hasPassWinners && superJackpotPool > 0) {
            uint256 dailyPayout = (superJackpotPool * dailyPayoutPercent) / 10000;
            round.dailyPayout = dailyPayout;
            superJackpotPool -= dailyPayout;
        }

        round.claimsOpen = true;

        emit RoundSettled(_roundId, _merkleRoot, round.dailyPayout);

        // Create next round
        currentRoundId++;
        rounds[currentRoundId] = Round({
            id: currentRoundId,
            startTime: block.timestamp,
            endTime: 0,
            totalPasses: 0,
            totalPassFunds: 0,
            surplusContributed: 0,
            dailyPayout: 0,
            merkleRoot: bytes32(0),
            settled: false,
            claimsOpen: false
        });

        emit RoundCreated(currentRoundId, block.timestamp);
    }

    // ============ Merkle Claims ============

    /**
     * @dev Claim jackpot winnings using Merkle proof
     * @param _roundId Round to claim from
     * @param _amount Amount to claim
     * @param _merkleProof Merkle proof for verification
     */
    function claimJackpot(
        uint256 _roundId,
        uint256 _amount,
        bytes32[] calldata _merkleProof
    ) external nonReentrant roundExists(_roundId) {
        Round storage round = rounds[_roundId];
        require(round.claimsOpen, "Claims not open");
        require(!roundClaimed[_roundId][msg.sender], "Already claimed");

        // Verify Merkle proof
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender, _amount));
        require(MerkleProof.verify(_merkleProof, round.merkleRoot, leaf), "Invalid proof");

        // Mark as claimed
        roundClaimed[_roundId][msg.sender] = true;
        totalClaimsPaid += _amount;

        // Transfer winnings
        paymentToken.safeTransfer(msg.sender, _amount);

        emit ClaimProcessed(_roundId, msg.sender, _amount);
    }

    // ============ Admin Functions ============

    function setLaBolitaVRF(address _laBolitaVRF) external onlyOwner {
        require(_laBolitaVRF != address(0), "Invalid address");
        laBolitaVRF = _laBolitaVRF;
        emit LaBolitaVRFUpdated(_laBolitaVRF);
    }

    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Invalid address");
        treasury = _treasury;
        emit TreasuryUpdated(_treasury);
    }

    function setPassFeePercent(uint256 _percent) external onlyOwner {
        require(_percent <= 2000, "Max 20%");
        passFeePercent = _percent;
    }

    function setSurplusFeePercent(uint256 _percent) external onlyOwner {
        require(_percent <= 2000, "Max 20%");
        surplusFeePercent = _percent;
    }

    function setDailyPayoutPercent(uint256 _percent) external onlyOwner {
        require(_percent <= 2000, "Max 20%");
        dailyPayoutPercent = _percent;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ View Functions ============

    function getPoolBalance() external view returns (uint256) {
        return superJackpotPool;
    }

    function getCurrentRound() external view returns (Round memory) {
        return rounds[currentRoundId];
    }

    function getRound(uint256 _roundId) external view returns (Round memory) {
        return rounds[_roundId];
    }

    function getPassInfo(uint256 _ticketId) external view returns (PassInfo memory) {
        return ticketPasses[_ticketId];
    }

    function hasClaimedRound(uint256 _roundId, address _user) external view returns (bool) {
        return roundClaimed[_roundId][_user];
    }

    function getStats() external view returns (
        uint256 poolBalance,
        uint256 totalPasses,
        uint256 totalClaims,
        uint256 currentRound
    ) {
        return (superJackpotPool, totalPassesRegistered, totalClaimsPaid, currentRoundId);
    }

    // ============ Emergency ============

    function emergencyWithdraw(address _token) external onlyOwner {
        IERC20 token = IERC20(_token);
        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(owner(), balance);
    }
}

contracts/LaBolitaVRF.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";

// Interface for JackpotManager
interface IJackpotManager {
    function registerPassTickets(uint256[] calldata ticketIds, uint256 totalPassAmount) external;
}

/**
 * @title LaBolitaVRF
 * @dev Loteria Web3 Non-Custodial con Chainlink VRF
 *
 * Caracteristicas:
 * - Solo cripto (USDT Polygon)
 * - Sin fiat, sin pasarelas
 * - Apuestas directas (sin deposito previo)
 * - VRF verificable (Chainlink)
 * - Flujo: Wallet -> Contrato -> Premio
 */
contract LaBolitaVRF is Ownable, ReentrancyGuard, Pausable, VRFConsumerBaseV2 {
    using SafeERC20 for IERC20;

    // ============ Chainlink VRF ============

    VRFCoordinatorV2Interface public vrfCoordinator;
    uint64 public vrfSubscriptionId;
    bytes32 public vrfKeyHash;
    uint32 public vrfCallbackGasLimit = 500000;
    uint16 public vrfRequestConfirmations = 3;

    // Tracking de requests VRF
    mapping(uint256 => uint256) public vrfRequestToDrawId;      // requestId => drawId
    mapping(uint256 => uint256) public vrfRequestToLotteryId;   // requestId => lotteryId
    mapping(uint256 => bool) public vrfRequestPending;

    event VRFRequested(uint256 indexed requestId, uint256 indexed drawId, bool isLottery);
    event VRFReceived(uint256 indexed requestId, uint256 randomWord);

    // ============ Estructuras ============

    enum BetType { FIJO, CENTENA, PARLE }
    enum DrawStatus { SCHEDULED, OPEN, CLOSED, PENDING_VRF, COMPLETED, CANCELLED }

    struct Bet {
        address player;
        uint256 drawId;
        BetType betType;
        string numbers;
        uint256 amount;
        bool claimed;
        uint256 payout;
    }

    struct Draw {
        uint256 id;
        string drawNumber;
        uint256 scheduledTime;
        DrawStatus status;
        string winningFijos;
        string winningCentenas;
        string winningParles;
        uint256 totalBets;
        uint256 totalAmount;
        uint256 totalPaidOut;
        uint256 vrfRequestId;    // ID del request VRF
        uint256 vrfRandomWord;   // Numero aleatorio recibido
    }

    // ============ La Fortuna - Loteria ============
    // Rules: 6 numbers from 1-49 + key number 0-9
    // 8 prize categories, jackpot cap $1,000,000

    enum LotteryStatus { SCHEDULED, OPEN, CLOSED, PENDING_VRF, COMPLETED }

    struct LotteryTicket {
        address player;
        uint256 lotteryId;
        uint8[6] numbers;      // 6 numbers from 1-49
        uint8 keyNumber;       // 0-9
        uint256 purchaseTime;
        bool claimed;
        uint256 prize;
        uint8 category;        // 1-8 (0 = no prize)
    }

    struct LotteryDraw {
        uint256 id;
        string drawName;
        uint256 scheduledTime;
        LotteryStatus status;
        uint8[6] winningNumbers;   // 6 winning numbers
        uint8 keyNumber;           // Winning key number
        uint256 jackpot;
        uint256 totalTickets;
        uint256 totalCollected;
        uint256 totalPaidOut;
        bool jackpotWon;
        uint256 vrfRequestId;
        uint256 vrfRandomWord;
    }

    // ============ Variables de Estado ============

    IERC20 public paymentToken;

    // ============ ESCROW - User Balances ============
    mapping(address => uint256) public userBalances;

    // ============ Jackpot Pass Integration ============
    address public jackpotManager;
    uint256 public passPrice = 1 * 10**6;  // 1 USDT per pass

    // Configuracion de apuestas
    uint256 public minBetAmount = 1 * 10**6;
    uint256 public maxBetAmount = 1000 * 10**6;
    uint256 public fijoMultiplier = 800000;
    uint256 public centenaMultiplier = 5000000;
    uint256 public parleMultiplier = 9000000;

    // Contadores
    uint256 public drawCounter;
    uint256 public betCounter;
    uint256 public lotteryCounter;
    uint256 public ticketCounter;

    // Mappings principales
    mapping(uint256 => Draw) public draws;
    mapping(uint256 => Bet) public bets;
    mapping(address => uint256[]) public userBets;
    mapping(uint256 => uint256[]) public drawBets;

    // Loteria
    mapping(uint256 => LotteryDraw) public lotteryDraws;
    mapping(uint256 => LotteryTicket) public lotteryTickets;
    mapping(address => uint256[]) public userLotteryTickets;
    mapping(uint256 => uint256[]) public lotteryDrawTickets;

    // Jackpot Configuration
    uint256 public currentJackpot;
    uint256 public lotteryTicketPrice = 1 * 10**6;      // 1 USDT
    uint256 public jackpotPercentage = 5000;            // 50% to jackpot
    uint256 public minJackpot = 10000 * 10**6;          // $10,000 minimum start
    uint256 public jackpotCap = 1000000 * 10**6;        // $1,000,000 cap

    // 8 Prize Categories (in USDT with 6 decimals)
    // Category 1: 6 + key = Jackpot (dynamic)
    // Category 2: 6 numbers = $100,000
    // Category 3: 5 + key = $10,000
    // Category 4: 5 numbers = $1,000
    // Category 5: 4 + key = $100
    // Category 6: 4 numbers = $50
    // Category 7: 3 + key = $10
    // Category 8: 3 numbers = $5
    uint256 public prize2Amount = 100000 * 10**6;   // 6 matches, no key
    uint256 public prize3Amount = 10000 * 10**6;    // 5 + key
    uint256 public prize4Amount = 1000 * 10**6;     // 5 matches
    uint256 public prize5Amount = 100 * 10**6;      // 4 + key
    uint256 public prize6Amount = 50 * 10**6;       // 4 matches
    uint256 public prize7Amount = 10 * 10**6;       // 3 + key
    uint256 public prize8Amount = 5 * 10**6;        // 3 matches

    // Admins
    mapping(address => bool) public admins;

    // Automatizacion
    uint256 public bettingCloseTime = 3600;
    uint256[3] public drawTimes = [36000, 54000, 75600];
    string[3] public drawNames = ["MANANA", "TARDE", "NOCHE"];

    // Sistema de referidos
    uint256 public referralBonusPercent = 500;

    struct ReferralInfo {
        address referrer;
        uint256 totalReferred;
        uint256 totalEarnings;
        uint256 pendingEarnings;
        uint256 joinedAt;
    }

    mapping(address => ReferralInfo) public referralInfo;
    mapping(address => address[]) public referredUsers;

    // ============ Eventos ============

    event BetPlacedDirect(uint256 indexed betId, address indexed player, uint256 indexed drawId, BetType betType, string numbers, uint256 amount);
    event LotteryTicketPurchasedDirect(uint256 indexed ticketId, address indexed player, uint256 indexed lotteryId, uint8[6] numbers, uint8 keyNumber);
    event DrawCreated(uint256 indexed drawId, string drawNumber, uint256 scheduledTime);
    event DrawStatusChanged(uint256 indexed drawId, DrawStatus status);
    event ResultsSubmitted(uint256 indexed drawId, string fijos, string centenas, string parles);
    event WinningsClaimed(uint256 indexed betId, address indexed player, uint256 amount);
    event LotteryDrawCreated(uint256 indexed lotteryId, string drawName, uint256 scheduledTime, uint256 jackpot);
    event LotteryResultsGenerated(uint256 indexed lotteryId, uint8[6] winningNumbers, uint8 keyNumber);
    event LotteryPrizeClaimed(uint256 indexed ticketId, address indexed player, uint8 category, uint256 amount);
    event JackpotWon(uint256 indexed lotteryId, address indexed winner, uint256 amount);
    event JackpotIncreased(uint256 newJackpot, uint256 addedAmount);
    event JackpotCapReached(uint256 jackpotAmount);
    event ReferralBonusEarned(address indexed referrer, address indexed referred, uint256 amount);
    event AdminUpdated(address indexed admin, bool status);

    // Escrow & Batch Checkout Events
    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event BatchCheckout(address indexed user, uint256[] betIds, uint256[] ticketIds, uint256 totalCost);
    event PassPurchased(uint256 indexed ticketId, address indexed user, uint256 passAmount);

    // ============ Modificadores ============

    modifier onlyAdmin() {
        require(admins[msg.sender] || msg.sender == owner(), "Not admin");
        _;
    }

    modifier drawExists(uint256 _drawId) {
        require(_drawId > 0 && _drawId <= drawCounter, "Draw does not exist");
        _;
    }

    modifier lotteryExists(uint256 _lotteryId) {
        require(_lotteryId > 0 && _lotteryId <= lotteryCounter, "Lottery does not exist");
        _;
    }

    // ============ Constructor ============

    constructor(
        address _paymentToken,
        address _vrfCoordinator,
        uint64 _subscriptionId,
        bytes32 _keyHash
    ) Ownable(msg.sender) VRFConsumerBaseV2(_vrfCoordinator) {
        paymentToken = IERC20(_paymentToken);
        vrfCoordinator = VRFCoordinatorV2Interface(_vrfCoordinator);
        vrfSubscriptionId = _subscriptionId;
        vrfKeyHash = _keyHash;
        admins[msg.sender] = true;
        currentJackpot = minJackpot;
    }

    // ============ APUESTAS DIRECTAS (Non-Custodial) ============

    /**
     * @dev Apostar directamente sin deposito previo
     * USDT se transfiere en la misma transaccion
     */
    function placeBetDirect(
        uint256 _drawId,
        BetType _betType,
        string calldata _numbers,
        uint256 _amount
    ) external nonReentrant whenNotPaused drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.OPEN, "Draw is not open");
        require(block.timestamp < draw.scheduledTime - bettingCloseTime, "Betting closed");
        require(_amount >= minBetAmount && _amount <= maxBetAmount, "Invalid amount");

        // Validar numeros segun tipo
        _validateNumbers(_betType, _numbers);

        // Transferir USDT directamente del usuario
        paymentToken.safeTransferFrom(msg.sender, address(this), _amount);

        // Crear apuesta
        betCounter++;
        bets[betCounter] = Bet({
            player: msg.sender,
            drawId: _drawId,
            betType: _betType,
            numbers: _numbers,
            amount: _amount,
            claimed: false,
            payout: 0
        });

        userBets[msg.sender].push(betCounter);
        drawBets[_drawId].push(betCounter);

        draw.totalBets++;
        draw.totalAmount += _amount;

        // Procesar referido si aplica
        _processReferralBonus(msg.sender, _amount);

        emit BetPlacedDirect(betCounter, msg.sender, _drawId, _betType, _numbers, _amount);
    }

    /**
     * @dev Buy lottery ticket directly (non-custodial)
     * Numbers: 6 unique numbers from 1-49
     * Key: 0-9
     */
    function buyLotteryTicketDirect(
        uint256 _lotteryId,
        uint8[6] calldata _numbers,
        uint8 _keyNumber
    ) external nonReentrant whenNotPaused lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        require(lottery.status == LotteryStatus.OPEN, "Lottery not open");
        require(block.timestamp < lottery.scheduledTime - bettingCloseTime, "Purchases closed");

        // Validate numbers (6 numbers 1-49, key 0-9)
        _validateLotteryNumbers(_numbers, _keyNumber);

        // Transfer ticket price directly from user
        paymentToken.safeTransferFrom(msg.sender, address(this), lotteryTicketPrice);

        // Create ticket
        ticketCounter++;
        lotteryTickets[ticketCounter] = LotteryTicket({
            player: msg.sender,
            lotteryId: _lotteryId,
            numbers: _numbers,
            keyNumber: _keyNumber,
            purchaseTime: block.timestamp,
            claimed: false,
            prize: 0,
            category: 0
        });

        userLotteryTickets[msg.sender].push(ticketCounter);
        lotteryDrawTickets[_lotteryId].push(ticketCounter);

        // Update lottery
        lottery.totalTickets++;
        lottery.totalCollected += lotteryTicketPrice;

        // Add to jackpot (with cap)
        uint256 toJackpot = (lotteryTicketPrice * jackpotPercentage) / 10000;
        if (currentJackpot + toJackpot > jackpotCap) {
            toJackpot = jackpotCap - currentJackpot;
        }
        if (toJackpot > 0) {
            currentJackpot += toJackpot;
            emit JackpotIncreased(currentJackpot, toJackpot);
        }

        // Process referral bonus
        _processReferralBonus(msg.sender, lotteryTicketPrice);

        emit LotteryTicketPurchasedDirect(ticketCounter, msg.sender, _lotteryId, _numbers, _keyNumber);
    }

    // ============ ESCROW FUNCTIONS ============

    /**
     * @dev Deposit USDT to escrow balance
     */
    function deposit(uint256 _amount) external nonReentrant whenNotPaused {
        require(_amount > 0, "Amount must be > 0");
        paymentToken.safeTransferFrom(msg.sender, address(this), _amount);
        userBalances[msg.sender] += _amount;
        emit Deposited(msg.sender, _amount);
    }

    /**
     * @dev Withdraw USDT from escrow balance
     */
    function withdraw(uint256 _amount) external nonReentrant {
        require(userBalances[msg.sender] >= _amount, "Insufficient balance");
        userBalances[msg.sender] -= _amount;
        paymentToken.safeTransfer(msg.sender, _amount);
        emit Withdrawn(msg.sender, _amount);
    }

    /**
     * @dev Get user's escrow balance
     */
    function getBalance(address _user) external view returns (uint256) {
        return userBalances[_user];
    }

    // ============ BATCH CHECKOUT WITH PASS ============

    struct BetInput {
        uint256 drawId;
        BetType betType;
        string numbers;
        uint256 amount;
    }

    struct TicketInput {
        uint256 lotteryId;
        uint8[6] numbers;
        uint8 keyNumber;
        bool includePass;  // true = +1 USDT jackpot pass
    }

    /**
     * @dev Batch checkout - one transaction for entire cart
     * Supports Jackpot Pass selection per ticket
     * @param _bets Array of La Bolita bets
     * @param _tickets Array of lottery tickets (with pass flag)
     * @return betIds Array of created bet IDs
     * @return ticketIds Array of created ticket IDs
     */
    function batchCheckout(
        BetInput[] calldata _bets,
        TicketInput[] calldata _tickets
    ) external nonReentrant whenNotPaused returns (uint256[] memory betIds, uint256[] memory ticketIds) {
        require(_bets.length > 0 || _tickets.length > 0, "Empty cart");

        // 1. Calculate total cost
        uint256 totalCost = 0;
        uint256 totalPassAmount = 0;
        uint256 passCount = 0;

        for (uint i = 0; i < _bets.length; i++) {
            totalCost += _bets[i].amount;
        }

        for (uint i = 0; i < _tickets.length; i++) {
            totalCost += lotteryTicketPrice;
            if (_tickets[i].includePass) {
                totalCost += passPrice;
                totalPassAmount += passPrice;
                passCount++;
            }
        }

        // 2. Verify escrow balance
        require(userBalances[msg.sender] >= totalCost, "Insufficient escrow balance");

        // 3. Deduct from escrow
        userBalances[msg.sender] -= totalCost;

        // 4. Process La Bolita bets
        betIds = new uint256[](_bets.length);
        for (uint i = 0; i < _bets.length; i++) {
            betIds[i] = _placeBetInternal(_bets[i]);
        }

        // 5. Process lottery tickets
        ticketIds = new uint256[](_tickets.length);
        uint256[] memory passTicketIds = new uint256[](passCount);
        uint256 passIndex = 0;

        for (uint i = 0; i < _tickets.length; i++) {
            ticketIds[i] = _buyTicketInternal(_tickets[i]);
            if (_tickets[i].includePass) {
                passTicketIds[passIndex] = ticketIds[i];
                passIndex++;
                emit PassPurchased(ticketIds[i], msg.sender, passPrice);
            }
        }

        // 6. Transfer passes to JackpotManager (real USDT transfer)
        if (totalPassAmount > 0 && jackpotManager != address(0)) {
            paymentToken.safeTransfer(jackpotManager, totalPassAmount);
            IJackpotManager(jackpotManager).registerPassTickets(passTicketIds, totalPassAmount);
        }

        emit BatchCheckout(msg.sender, betIds, ticketIds, totalCost);
    }

    /**
     * @dev Internal: Place bet from escrow (no transfer needed)
     */
    function _placeBetInternal(BetInput calldata _input) internal returns (uint256) {
        require(_input.drawId > 0 && _input.drawId <= drawCounter, "Draw does not exist");
        Draw storage draw = draws[_input.drawId];
        require(draw.status == DrawStatus.OPEN, "Draw not open");
        require(block.timestamp < draw.scheduledTime - bettingCloseTime, "Betting closed");
        require(_input.amount >= minBetAmount && _input.amount <= maxBetAmount, "Invalid amount");
        _validateNumbers(_input.betType, _input.numbers);

        betCounter++;
        bets[betCounter] = Bet({
            player: msg.sender,
            drawId: _input.drawId,
            betType: _input.betType,
            numbers: _input.numbers,
            amount: _input.amount,
            claimed: false,
            payout: 0
        });

        userBets[msg.sender].push(betCounter);
        drawBets[_input.drawId].push(betCounter);
        draw.totalBets++;
        draw.totalAmount += _input.amount;

        _processReferralBonus(msg.sender, _input.amount);

        emit BetPlacedDirect(betCounter, msg.sender, _input.drawId, _input.betType, _input.numbers, _input.amount);
        return betCounter;
    }

    /**
     * @dev Internal: Buy lottery ticket from escrow (no transfer needed)
     */
    function _buyTicketInternal(TicketInput calldata _input) internal returns (uint256) {
        require(_input.lotteryId > 0 && _input.lotteryId <= lotteryCounter, "Lottery does not exist");
        LotteryDraw storage lottery = lotteryDraws[_input.lotteryId];
        require(lottery.status == LotteryStatus.OPEN, "Lottery not open");
        require(block.timestamp < lottery.scheduledTime - bettingCloseTime, "Purchases closed");
        _validateLotteryNumbers(_input.numbers, _input.keyNumber);

        ticketCounter++;
        lotteryTickets[ticketCounter] = LotteryTicket({
            player: msg.sender,
            lotteryId: _input.lotteryId,
            numbers: _input.numbers,
            keyNumber: _input.keyNumber,
            purchaseTime: block.timestamp,
            claimed: false,
            prize: 0,
            category: 0
        });

        userLotteryTickets[msg.sender].push(ticketCounter);
        lotteryDrawTickets[_input.lotteryId].push(ticketCounter);
        lottery.totalTickets++;
        lottery.totalCollected += lotteryTicketPrice;

        // Jackpot contribution (existing logic)
        uint256 toJackpot = (lotteryTicketPrice * jackpotPercentage) / 10000;
        if (currentJackpot + toJackpot <= jackpotCap) {
            currentJackpot += toJackpot;
            emit JackpotIncreased(currentJackpot, toJackpot);
        }

        _processReferralBonus(msg.sender, lotteryTicketPrice);

        emit LotteryTicketPurchasedDirect(ticketCounter, msg.sender, _input.lotteryId, _input.numbers, _input.keyNumber);
        return ticketCounter;
    }

    // ============ CHAINLINK VRF ============

    /**
     * @dev Solicitar numero aleatorio para sorteo de La Bolita
     */
    function requestDrawResults(uint256 _drawId) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.CLOSED, "Draw must be closed first");
        require(!vrfRequestPending[draw.vrfRequestId], "VRF already requested");

        uint256 requestId = vrfCoordinator.requestRandomWords(
            vrfKeyHash,
            vrfSubscriptionId,
            vrfRequestConfirmations,
            vrfCallbackGasLimit,
            1 // numWords
        );

        draw.vrfRequestId = requestId;
        draw.status = DrawStatus.PENDING_VRF;
        vrfRequestToDrawId[requestId] = _drawId;
        vrfRequestPending[requestId] = true;

        emit VRFRequested(requestId, _drawId, false);
        emit DrawStatusChanged(_drawId, DrawStatus.PENDING_VRF);
    }

    /**
     * @dev Solicitar numero aleatorio para sorteo de loteria
     */
    function requestLotteryResults(uint256 _lotteryId) external onlyAdmin lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        require(lottery.status == LotteryStatus.CLOSED, "Lottery must be closed first");
        require(!vrfRequestPending[lottery.vrfRequestId], "VRF already requested");

        uint256 requestId = vrfCoordinator.requestRandomWords(
            vrfKeyHash,
            vrfSubscriptionId,
            vrfRequestConfirmations,
            vrfCallbackGasLimit,
            1
        );

        lottery.vrfRequestId = requestId;
        lottery.status = LotteryStatus.PENDING_VRF;
        vrfRequestToLotteryId[requestId] = _lotteryId;
        vrfRequestPending[requestId] = true;

        emit VRFRequested(requestId, _lotteryId, true);
    }

    /**
     * @dev Callback de Chainlink VRF
     */
    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
        require(vrfRequestPending[requestId], "Request not found");
        vrfRequestPending[requestId] = false;

        uint256 randomWord = randomWords[0];
        emit VRFReceived(requestId, randomWord);

        // Verificar si es sorteo de La Bolita
        uint256 drawId = vrfRequestToDrawId[requestId];
        if (drawId > 0) {
            _processDrawVRF(drawId, randomWord);
            return;
        }

        // Verificar si es sorteo de loteria
        uint256 lotteryId = vrfRequestToLotteryId[requestId];
        if (lotteryId > 0) {
            _processLotteryVRF(lotteryId, randomWord);
        }
    }

    /**
     * @dev Procesar resultado VRF para La Bolita
     */
    function _processDrawVRF(uint256 _drawId, uint256 _randomWord) internal {
        Draw storage draw = draws[_drawId];
        draw.vrfRandomWord = _randomWord;

        // Generar numeros ganadores desde VRF
        uint256 seed = _randomWord;

        // Fijo: 2 digitos (00-99)
        uint256 fijo = seed % 100;
        draw.winningFijos = _uint2str2(fijo);

        seed = uint256(keccak256(abi.encodePacked(seed)));

        // Centena: 3 digitos (000-999)
        uint256 centena = seed % 1000;
        draw.winningCentenas = _uint2str3(centena);

        seed = uint256(keccak256(abi.encodePacked(seed)));

        // Parle: 4 digitos (0000-9999)
        uint256 parle = seed % 10000;
        draw.winningParles = _uint2str4(parle);

        draw.status = DrawStatus.COMPLETED;

        emit ResultsSubmitted(_drawId, draw.winningFijos, draw.winningCentenas, draw.winningParles);
        emit DrawStatusChanged(_drawId, DrawStatus.COMPLETED);
    }

    /**
     * @dev Process VRF result for Lottery
     * Generates 6 unique numbers from 1-49 + key number 0-9
     */
    function _processLotteryVRF(uint256 _lotteryId, uint256 _randomWord) internal {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        lottery.vrfRandomWord = _randomWord;

        uint256 seed = _randomWord;

        // Generate 6 unique numbers from 1-49
        uint8[6] memory winningNumbers;
        uint8 count = 0;

        while (count < 6) {
            uint8 num = uint8((seed % 49) + 1);  // 1-49
            seed = uint256(keccak256(abi.encodePacked(seed)));

            bool duplicate = false;
            for (uint8 i = 0; i < count; i++) {
                if (winningNumbers[i] == num) {
                    duplicate = true;
                    break;
                }
            }

            if (!duplicate) {
                winningNumbers[count] = num;
                count++;
            }
        }

        // Sort numbers ascending
        for (uint8 i = 0; i < 5; i++) {
            for (uint8 j = i + 1; j < 6; j++) {
                if (winningNumbers[i] > winningNumbers[j]) {
                    uint8 temp = winningNumbers[i];
                    winningNumbers[i] = winningNumbers[j];
                    winningNumbers[j] = temp;
                }
            }
        }

        // Key number (0-9)
        uint8 keyNum = uint8(seed % 10);

        lottery.winningNumbers = winningNumbers;
        lottery.keyNumber = keyNum;
        lottery.jackpot = currentJackpot;
        lottery.status = LotteryStatus.COMPLETED;

        emit LotteryResultsGenerated(_lotteryId, winningNumbers, keyNum);
    }

    // ============ CLAIMS ============

    /**
     * @dev Reclamar premio de apuesta
     */
    function claimWinnings(uint256 _betId) external nonReentrant {
        Bet storage bet = bets[_betId];
        require(bet.player == msg.sender, "Not your bet");
        require(!bet.claimed, "Already claimed");

        Draw storage draw = draws[bet.drawId];
        require(draw.status == DrawStatus.COMPLETED, "Draw not completed");

        uint256 payout = _calculatePayout(bet, draw);
        require(payout > 0, "No winnings");

        bet.claimed = true;
        bet.payout = payout;
        draw.totalPaidOut += payout;

        // Transferir premio directamente
        paymentToken.safeTransfer(msg.sender, payout);

        emit WinningsClaimed(_betId, msg.sender, payout);
    }

    /**
     * @dev Reclamar premio de loteria
     */
    function claimLotteryPrize(uint256 _ticketId) external nonReentrant {
        LotteryTicket storage ticket = lotteryTickets[_ticketId];
        require(ticket.player == msg.sender, "Not your ticket");
        require(!ticket.claimed, "Already claimed");

        LotteryDraw storage lottery = lotteryDraws[ticket.lotteryId];
        require(lottery.status == LotteryStatus.COMPLETED, "Lottery not completed");

        (uint8 category, uint256 prize) = _calculateLotteryPrize(ticket, lottery);
        require(prize > 0, "No prize");

        ticket.claimed = true;
        ticket.prize = prize;
        ticket.category = category;
        lottery.totalPaidOut += prize;

        // Si gano jackpot
        if (category == 1 && !lottery.jackpotWon) {
            lottery.jackpotWon = true;
            currentJackpot = minJackpot;
            emit JackpotWon(ticket.lotteryId, msg.sender, prize);
        }

        paymentToken.safeTransfer(msg.sender, prize);
        emit LotteryPrizeClaimed(_ticketId, msg.sender, category, prize);
    }

    // ============ ADMIN - Sorteos ============

    function createDraw(string calldata _drawNumber, uint256 _scheduledTime) external onlyAdmin {
        require(_scheduledTime > block.timestamp, "Must be future");

        drawCounter++;
        draws[drawCounter] = Draw({
            id: drawCounter,
            drawNumber: _drawNumber,
            scheduledTime: _scheduledTime,
            status: DrawStatus.OPEN,
            winningFijos: "",
            winningCentenas: "",
            winningParles: "",
            totalBets: 0,
            totalAmount: 0,
            totalPaidOut: 0,
            vrfRequestId: 0,
            vrfRandomWord: 0
        });

        emit DrawCreated(drawCounter, _drawNumber, _scheduledTime);
    }

    function closeDraw(uint256 _drawId) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.OPEN, "Not open");
        draw.status = DrawStatus.CLOSED;
        emit DrawStatusChanged(_drawId, DrawStatus.CLOSED);
    }

    function createLotteryDraw(string calldata _drawName, uint256 _scheduledTime) external onlyAdmin {
        require(_scheduledTime > block.timestamp, "Must be future");

        lotteryCounter++;
        lotteryDraws[lotteryCounter] = LotteryDraw({
            id: lotteryCounter,
            drawName: _drawName,
            scheduledTime: _scheduledTime,
            status: LotteryStatus.OPEN,
            winningNumbers: [uint8(0), uint8(0), uint8(0), uint8(0), uint8(0), uint8(0)],  // 6 numbers
            keyNumber: 0,
            jackpot: 0,
            totalTickets: 0,
            totalCollected: 0,
            totalPaidOut: 0,
            jackpotWon: false,
            vrfRequestId: 0,
            vrfRandomWord: 0
        });

        emit LotteryDrawCreated(lotteryCounter, _drawName, _scheduledTime, currentJackpot);
    }

    function closeLotteryDraw(uint256 _lotteryId) external onlyAdmin lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        require(lottery.status == LotteryStatus.OPEN, "Not open");
        lottery.status = LotteryStatus.CLOSED;
    }

    // ============ ADMIN - Config ============

    function setAdmin(address _admin, bool _status) external onlyOwner {
        admins[_admin] = _status;
        emit AdminUpdated(_admin, _status);
    }

    function setVRFConfig(
        uint64 _subscriptionId,
        bytes32 _keyHash,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations
    ) external onlyOwner {
        vrfSubscriptionId = _subscriptionId;
        vrfKeyHash = _keyHash;
        vrfCallbackGasLimit = _callbackGasLimit;
        vrfRequestConfirmations = _requestConfirmations;
    }

    function setBetLimits(uint256 _min, uint256 _max) external onlyOwner {
        minBetAmount = _min;
        maxBetAmount = _max;
    }

    function setLotteryTicketPrice(uint256 _price) external onlyOwner {
        lotteryTicketPrice = _price;
    }

    function setJackpotCap(uint256 _cap) external onlyOwner {
        require(_cap >= currentJackpot, "Cap must be >= current jackpot");
        jackpotCap = _cap;
    }

    function setMinJackpot(uint256 _min) external onlyOwner {
        minJackpot = _min;
    }

    function setLotteryPrizes(
        uint256 _prize2,
        uint256 _prize3,
        uint256 _prize4,
        uint256 _prize5,
        uint256 _prize6,
        uint256 _prize7,
        uint256 _prize8
    ) external onlyOwner {
        prize2Amount = _prize2;
        prize3Amount = _prize3;
        prize4Amount = _prize4;
        prize5Amount = _prize5;
        prize6Amount = _prize6;
        prize7Amount = _prize7;
        prize8Amount = _prize8;
    }

    function setReferralBonus(uint256 _percent) external onlyOwner {
        require(_percent <= 1000, "Max 10%");
        referralBonusPercent = _percent;
    }

    function setJackpotManager(address _manager) external onlyOwner {
        jackpotManager = _manager;
    }

    function setPassPrice(uint256 _price) external onlyOwner {
        passPrice = _price;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ Funciones Internas ============

    function _validateNumbers(BetType _type, string calldata _numbers) internal pure {
        bytes memory b = bytes(_numbers);
        if (_type == BetType.FIJO) require(b.length == 2, "Fijo: 2 digits");
        else if (_type == BetType.CENTENA) require(b.length == 3, "Centena: 3 digits");
        else require(b.length == 4, "Parle: 4 digits");

        for (uint i = 0; i < b.length; i++) {
            require(b[i] >= 0x30 && b[i] <= 0x39, "Only digits");
        }
    }

    function _validateLotteryNumbers(uint8[6] calldata _nums, uint8 _key) internal pure {
        require(_key <= 9, "Key must be 0-9");
        for (uint8 i = 0; i < 6; i++) {
            require(_nums[i] >= 1 && _nums[i] <= 49, "Numbers must be 1-49");
            for (uint8 j = i + 1; j < 6; j++) {
                require(_nums[i] != _nums[j], "No duplicates");
            }
        }
    }

    function _calculatePayout(Bet storage bet, Draw storage draw) internal view returns (uint256) {
        uint256 multiplier;
        string memory winning;

        if (bet.betType == BetType.FIJO) {
            multiplier = fijoMultiplier;
            winning = draw.winningFijos;
        } else if (bet.betType == BetType.CENTENA) {
            multiplier = centenaMultiplier;
            winning = draw.winningCentenas;
        } else {
            multiplier = parleMultiplier;
            winning = draw.winningParles;
        }

        if (keccak256(bytes(bet.numbers)) == keccak256(bytes(winning))) {
            return (bet.amount * multiplier) / 10000;
        }
        return 0;
    }

    /**
     * @dev Calculate lottery prize based on 8 categories
     * Cat 1: 6 + key = Jackpot
     * Cat 2: 6 numbers = $100,000
     * Cat 3: 5 + key = $10,000
     * Cat 4: 5 numbers = $1,000
     * Cat 5: 4 + key = $100
     * Cat 6: 4 numbers = $50
     * Cat 7: 3 + key = $10
     * Cat 8: 3 numbers = $5
     */
    function _calculateLotteryPrize(LotteryTicket storage ticket, LotteryDraw storage lottery)
        internal view returns (uint8 category, uint256 prize)
    {
        uint8 matches = _countMatches(ticket.numbers, lottery.winningNumbers);
        bool keyMatch = ticket.keyNumber == lottery.keyNumber;

        if (matches == 6 && keyMatch) return (1, lottery.jackpot);   // Jackpot
        if (matches == 6) return (2, prize2Amount);                  // 6 numbers
        if (matches == 5 && keyMatch) return (3, prize3Amount);      // 5 + key
        if (matches == 5) return (4, prize4Amount);                  // 5 numbers
        if (matches == 4 && keyMatch) return (5, prize5Amount);      // 4 + key
        if (matches == 4) return (6, prize6Amount);                  // 4 numbers
        if (matches == 3 && keyMatch) return (7, prize7Amount);      // 3 + key
        if (matches == 3) return (8, prize8Amount);                  // 3 numbers
        return (0, 0);  // No prize
    }

    function _countMatches(uint8[6] storage ticketNums, uint8[6] storage winningNums)
        internal view returns (uint8 count)
    {
        for (uint8 i = 0; i < 6; i++) {
            for (uint8 j = 0; j < 6; j++) {
                if (ticketNums[i] == winningNums[j]) {
                    count++;
                    break;
                }
            }
        }
    }

    function _processReferralBonus(address _player, uint256 _amount) internal {
        address referrer = referralInfo[_player].referrer;
        if (referrer != address(0) && referralBonusPercent > 0) {
            uint256 bonus = (_amount * referralBonusPercent) / 10000;
            referralInfo[referrer].pendingEarnings += bonus;
            referralInfo[referrer].totalEarnings += bonus;
            emit ReferralBonusEarned(referrer, _player, bonus);
        }
    }

    function _uint2str2(uint256 _num) internal pure returns (string memory) {
        bytes memory b = new bytes(2);
        b[0] = bytes1(uint8(48 + (_num / 10)));
        b[1] = bytes1(uint8(48 + (_num % 10)));
        return string(b);
    }

    function _uint2str3(uint256 _num) internal pure returns (string memory) {
        bytes memory b = new bytes(3);
        b[0] = bytes1(uint8(48 + (_num / 100)));
        b[1] = bytes1(uint8(48 + ((_num / 10) % 10)));
        b[2] = bytes1(uint8(48 + (_num % 10)));
        return string(b);
    }

    function _uint2str4(uint256 _num) internal pure returns (string memory) {
        bytes memory b = new bytes(4);
        b[0] = bytes1(uint8(48 + (_num / 1000)));
        b[1] = bytes1(uint8(48 + ((_num / 100) % 10)));
        b[2] = bytes1(uint8(48 + ((_num / 10) % 10)));
        b[3] = bytes1(uint8(48 + (_num % 10)));
        return string(b);
    }

    // ============ Views ============

    function getJackpot() external view returns (uint256) {
        return currentJackpot;
    }

    function getUserBets(address _user) external view returns (uint256[] memory) {
        return userBets[_user];
    }

    function getUserLotteryTickets(address _user) external view returns (uint256[] memory) {
        return userLotteryTickets[_user];
    }

    function getDrawBets(uint256 _drawId) external view returns (uint256[] memory) {
        return drawBets[_drawId];
    }

    function checkWinnings(uint256 _betId) external view returns (uint256) {
        Bet storage bet = bets[_betId];
        Draw storage draw = draws[bet.drawId];
        if (draw.status != DrawStatus.COMPLETED) return 0;
        return _calculatePayout(bet, draw);
    }

    function checkLotteryPrize(uint256 _ticketId) external view returns (uint8 category, uint256 prize) {
        LotteryTicket storage ticket = lotteryTickets[_ticketId];
        LotteryDraw storage lottery = lotteryDraws[ticket.lotteryId];
        if (lottery.status != LotteryStatus.COMPLETED) return (0, 0);
        return _calculateLotteryPrize(ticket, lottery);
    }

    // ============ Referidos ============

    function registerReferral(address _referrer) external {
        require(_referrer != msg.sender, "Cannot refer yourself");
        require(_referrer != address(0), "Invalid referrer");
        require(referralInfo[msg.sender].referrer == address(0), "Already registered");

        referralInfo[msg.sender].referrer = _referrer;
        referralInfo[msg.sender].joinedAt = block.timestamp;
        referralInfo[_referrer].totalReferred++;
        referredUsers[_referrer].push(msg.sender);
    }

    function claimReferralEarnings() external nonReentrant {
        uint256 pending = referralInfo[msg.sender].pendingEarnings;
        require(pending > 0, "No earnings");

        referralInfo[msg.sender].pendingEarnings = 0;
        paymentToken.safeTransfer(msg.sender, pending);
    }

    function getReferralInfo(address _user) external view returns (ReferralInfo memory) {
        return referralInfo[_user];
    }

    function getReferredUsers(address _user) external view returns (address[] memory) {
        return referredUsers[_user];
    }

    // ============ Emergency ============

    function emergencyWithdraw(address _token) external onlyOwner {
        IERC20 token = IERC20(_token);
        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(owner(), balance);
    }
}

