contracts/LaBolita.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title LaBolita
 * @dev Contrato de loteria descentralizado en Polygon
 * Soporta apuestas en USDT/USDC con sistema de sorteos
 */
contract LaBolita is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ============ Estructuras ============

    enum BetType { FIJO, CENTENA, PARLE }
    enum DrawStatus { SCHEDULED, OPEN, CLOSED, COMPLETED, CANCELLED }

    struct Bet {
        address player;
        uint256 drawId;
        BetType betType;
        string numbers;      // "00" para fijo, "000" para centena, "0000" para parle
        uint256 amount;
        bool claimed;
        uint256 payout;
    }

    struct Draw {
        uint256 id;
        string drawNumber;   // Identificador del sorteo (ej: "2024-01-15-AM")
        uint256 scheduledTime;
        DrawStatus status;
        string winningFijos;    // 2 digitos
        string winningCentenas; // 3 digitos
        string winningParles;   // 4 digitos
        uint256 totalBets;
        uint256 totalAmount;
        uint256 totalPaidOut;
    }

    // ============ LA FORTUNA - Estructuras ============

    enum LotteryStatus { SCHEDULED, OPEN, CLOSED, COMPLETED }

    struct LotteryTicket {
        address player;
        uint256 lotteryId;
        uint8[5] numbers;        // 5 numeros del 1-54
        uint8 keyNumber;         // Numero clave 0-9
        uint256 purchaseTime;
        bool claimed;
        uint256 prize;
        uint8 category;          // 0=sin premio, 1-6=categoria
    }

    struct LotteryDraw {
        uint256 id;
        string drawName;          // "FORTUNA-2024-W03-DOM"
        uint256 scheduledTime;
        LotteryStatus status;
        uint8[5] winningNumbers;  // 5 numeros ganadores (1-54)
        uint8 keyNumber;          // Numero clave ganador (0-9)
        uint256 jackpot;          // Bote acumulado para este sorteo
        uint256 totalTickets;
        uint256 totalCollected;
        uint256 totalPaidOut;
        bool jackpotWon;
    }

    // Categorias de premios La Fortuna (porcentaje del pozo en basis points)
    struct LotteryPrizeConfig {
