    // ============ Modificadores ============

    modifier onlyAdmin() {
        require(admins[msg.sender] || msg.sender == owner(), "Not admin");
        _;
    }

    modifier drawExists(uint256 _drawId) {
        require(_drawId > 0 && _drawId <= drawCounter, "Draw does not exist");
        _;
    }

    modifier lotteryExists(uint256 _lotteryId) {
        require(_lotteryId > 0 && _lotteryId <= lotteryCounter, "Lottery draw does not exist");
        _;
    }

    // ============ Constructor ============

    constructor(address _paymentToken) Ownable(msg.sender) {
        paymentToken = IERC20(_paymentToken);
        admins[msg.sender] = true;
    }

    // ============ Funciones de Usuario ============

    /**
     * @dev Depositar USDT/USDC al contrato
     */
    function deposit(uint256 _amount) external nonReentrant whenNotPaused {
        require(_amount > 0, "Amount must be > 0");

        paymentToken.safeTransferFrom(msg.sender, address(this), _amount);
        userBalances[msg.sender] += _amount;

        // Bono de bienvenida en primer deposito
        _processWelcomeBonus(msg.sender);

        emit Deposited(msg.sender, _amount);
    }

    /**
     * @dev Depositar en nombre de un usuario (solo owner - para desarrollo/testing)
     */
    function adminDeposit(address _user, uint256 _amount) external onlyOwner {
        require(_amount > 0, "Amount must be > 0");
        require(_user != address(0), "Invalid user address");

        userBalances[_user] += _amount;

        emit Deposited(_user, _amount);
    }

    /**
     * @dev Retirar fondos de un usuario (solo owner - para liquidación de Keno)
     */
    function adminWithdraw(address _user, uint256 _amount) external onlyOwner {
        require(_amount > 0, "Amount must be > 0");
        require(_user != address(0), "Invalid user address");
        require(userBalances[_user] >= _amount, "Insufficient user balance");

        userBalances[_user] -= _amount;

        emit Withdrawn(_user, _amount);
    }

    /**
     * @dev Liquidar sesión de Keno (ajuste neto positivo o negativo)
     * @param _user Dirección del usuario
     * @param _netAmount Cantidad neta (positivo = ganó, negativo = perdió)
     * @param _isProfit true si el usuario ganó, false si perdió
     */
    function settleKenoSession(
        address _user,
        uint256 _netAmount,
        bool _isProfit
    ) external onlyOwner {
        require(_user != address(0), "Invalid user address");

        if (_isProfit) {
            // Usuario ganó - agregar a su balance
            userBalances[_user] += _netAmount;
            emit Deposited(_user, _netAmount);
        } else {
            // Usuario perdió - restar de su balance
            require(userBalances[_user] >= _netAmount, "Insufficient user balance");
            userBalances[_user] -= _netAmount;
            emit Withdrawn(_user, _netAmount);
        }
    }

    /**
     * @dev Depositar con codigo de referido
     */
    function depositWithReferral(uint256 _amount, address _referrer) external nonReentrant whenNotPaused {
        require(_amount > 0, "Amount must be > 0");
        require(_referrer != msg.sender, "Cannot refer yourself");
        require(_referrer != address(0), "Invalid referrer");

        // Registrar referido si es nuevo
        if (referralInfo[msg.sender].referrer == address(0) && referralInfo[msg.sender].joinedAt == 0) {
            _registerReferral(msg.sender, _referrer);
        }

        paymentToken.safeTransferFrom(msg.sender, address(this), _amount);
        userBalances[msg.sender] += _amount;

        // Bono de bienvenida en primer deposito
        _processWelcomeBonus(msg.sender);

        emit Deposited(msg.sender, _amount);
    }

    /**
     * @dev Registrar referido (sin deposito)
     */
    function registerReferral(address _referrer) external {
        require(_referrer != msg.sender, "Cannot refer yourself");
        require(_referrer != address(0), "Invalid referrer");
        require(referralInfo[msg.sender].referrer == address(0), "Already has referrer");
        require(referralInfo[msg.sender].joinedAt == 0, "Already registered");

        _registerReferral(msg.sender, _referrer);
    }

    /**
     * @dev Reclamar ganancias de referidos
     */
    function claimReferralEarnings() external nonReentrant {
        uint256 pending = referralInfo[msg.sender].pendingEarnings;
        require(pending > 0, "No pending earnings");

        referralInfo[msg.sender].pendingEarnings = 0;
        userBalances[msg.sender] += pending;

        emit ReferralBonusClaimed(msg.sender, pending);
    }

    /**
     * @dev Retirar balance del contrato
     */
    function withdraw(uint256 _amount) external nonReentrant {
        require(_amount > 0, "Amount must be > 0");
        require(userBalances[msg.sender] >= _amount, "Insufficient balance");

        userBalances[msg.sender] -= _amount;
        paymentToken.safeTransfer(msg.sender, _amount);

        emit Withdrawn(msg.sender, _amount);
    }

    /**
     * @dev Realizar una apuesta
     */
    function placeBet(
        uint256 _drawId,
        BetType _betType,
        string calldata _numbers,
        uint256 _amount
    ) external nonReentrant whenNotPaused drawExists(_drawId) {
        Draw storage draw = draws[_drawId];

        require(draw.status == DrawStatus.OPEN, "Draw not open");
        require(_amount >= minBetAmount, "Below min bet");
        require(_amount <= maxBetAmount, "Above max bet");
        require(userBalances[msg.sender] >= _amount, "Insufficient balance");
        require(_validateNumbers(_betType, _numbers), "Invalid numbers");

        // Deducir del balance
        userBalances[msg.sender] -= _amount;

        // Crear apuesta
        betCounter++;
        bets[betCounter] = Bet({
            player: msg.sender,
            drawId: _drawId,
            betType: _betType,
            numbers: _numbers,
            amount: _amount,
            claimed: false,
            payout: 0
        });

        // Actualizar registros
        userBets[msg.sender].push(betCounter);
        drawBets[_drawId].push(betCounter);
        draw.totalBets++;
        draw.totalAmount += _amount;

        // Dar bonus al referidor
        _processReferralBonus(msg.sender, _amount);

        emit BetPlaced(betCounter, msg.sender, _drawId, _betType, _numbers, _amount);
    }

    /**
     * @dev Reclamar ganancias de una apuesta
     */
    function claimWinnings(uint256 _betId) external nonReentrant {
        require(_betId > 0 && _betId <= betCounter, "Bet does not exist");

        Bet storage bet = bets[_betId];
        require(bet.player == msg.sender, "Not your bet");
        require(!bet.claimed, "Already claimed");

        Draw storage draw = draws[bet.drawId];
        require(draw.status == DrawStatus.COMPLETED, "Draw not completed");

        // Calcular ganancia
        uint256 payout = _calculatePayout(bet, draw);

        bet.claimed = true;
        bet.payout = payout;

        if (payout > 0) {
            userBalances[msg.sender] += payout;
            draw.totalPaidOut += payout;
            emit WinningsClaimed(_betId, msg.sender, payout);
        }
    }

    /**
     * @dev Reclamar todas las ganancias pendientes de un sorteo
     */
    function claimAllWinnings(uint256 _drawId) external nonReentrant drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.COMPLETED, "Draw not completed");

        uint256[] memory userBetIds = userBets[msg.sender];
        uint256 totalPayout = 0;

        for (uint256 i = 0; i < userBetIds.length; i++) {
            Bet storage bet = bets[userBetIds[i]];

            if (bet.drawId == _drawId && !bet.claimed) {
                uint256 payout = _calculatePayout(bet, draw);
                bet.claimed = true;
                bet.payout = payout;
                totalPayout += payout;
            }
        }

        if (totalPayout > 0) {
            userBalances[msg.sender] += totalPayout;
            draw.totalPaidOut += totalPayout;
            emit WinningsClaimed(0, msg.sender, totalPayout);
        }
    }

    // ============ Funciones de Admin ============

    /**
     * @dev Crear un nuevo sorteo
     */
    function createDraw(
        string calldata _drawNumber,
        uint256 _scheduledTime
    ) external onlyAdmin returns (uint256) {
        drawCounter++;

        draws[drawCounter] = Draw({
            id: drawCounter,
            drawNumber: _drawNumber,
            scheduledTime: _scheduledTime,
            status: DrawStatus.SCHEDULED,
            winningFijos: "",
            winningCentenas: "",
            winningParles: "",
            totalBets: 0,
            totalAmount: 0,
            totalPaidOut: 0
        });

        emit DrawCreated(drawCounter, _drawNumber, _scheduledTime);
        return drawCounter;
    }

    /**
     * @dev Abrir sorteo para apuestas
     */
    function openDraw(uint256 _drawId) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.SCHEDULED, "Invalid status");

        draw.status = DrawStatus.OPEN;
        emit DrawStatusChanged(_drawId, DrawStatus.OPEN);
    }

    /**
     * @dev Cerrar sorteo (no mas apuestas)
     */
    function closeDraw(uint256 _drawId) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.OPEN, "Invalid status");

        draw.status = DrawStatus.CLOSED;
        emit DrawStatusChanged(_drawId, DrawStatus.CLOSED);
    }

    /**
     * @dev Ingresar resultados del sorteo
     */
    function submitResults(
        uint256 _drawId,
        string calldata _fijos,
        string calldata _centenas,
        string calldata _parles
    ) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status == DrawStatus.CLOSED, "Draw must be closed");
        require(bytes(_fijos).length == 2, "Fijos must be 2 digits");
        require(bytes(_centenas).length == 3, "Centenas must be 3 digits");
        require(bytes(_parles).length == 4, "Parles must be 4 digits");

        draw.winningFijos = _fijos;
        draw.winningCentenas = _centenas;
        draw.winningParles = _parles;
        draw.status = DrawStatus.COMPLETED;

        emit ResultsSubmitted(_drawId, _fijos, _centenas, _parles);
        emit DrawStatusChanged(_drawId, DrawStatus.COMPLETED);
    }

    /**
     * @dev Cancelar sorteo y reembolsar
     */
    function cancelDraw(uint256 _drawId) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];
        require(draw.status != DrawStatus.COMPLETED, "Cannot cancel completed");
        require(draw.status != DrawStatus.CANCELLED, "Already cancelled");

        draw.status = DrawStatus.CANCELLED;

        // Reembolsar todas las apuestas
        uint256[] memory betIds = drawBets[_drawId];
        for (uint256 i = 0; i < betIds.length; i++) {
            Bet storage bet = bets[betIds[i]];
            if (!bet.claimed) {
                userBalances[bet.player] += bet.amount;
                bet.claimed = true;
            }
        }

        emit DrawStatusChanged(_drawId, DrawStatus.CANCELLED);
    }

