    // ============ Sistema de Sorteos Automaticos ============

    /**
     * @dev Crear los 3 sorteos del dia automaticamente
     */
    function createDailyDraws() external onlyAdmin {
        uint256 today = block.timestamp - (block.timestamp % 86400); // Inicio del dia UTC

        require(lastDrawDate < today, "Draws already created for today");

        for (uint256 i = 0; i < 3; i++) {
            uint256 scheduledTime = today + drawTimes[i];

            // Solo crear si el sorteo es en el futuro
            if (scheduledTime > block.timestamp) {
                drawCounter++;

                // Formato: 2024-01-15-MANANA
                string memory drawNumber = string(abi.encodePacked(
                    _formatDate(today),
                    "-",
                    drawNames[i]
                ));

                draws[drawCounter] = Draw({
                    id: drawCounter,
                    drawNumber: drawNumber,
                    scheduledTime: scheduledTime,
                    status: DrawStatus.OPEN, // Abre inmediatamente
                    winningFijos: "",
                    winningCentenas: "",
                    winningParles: "",
                    totalBets: 0,
                    totalAmount: 0,
                    totalPaidOut: 0
                });

                emit DrawCreated(drawCounter, drawNumber, scheduledTime);
                emit AutoDrawCreated(drawCounter, drawNames[i], scheduledTime);
                emit DrawStatusChanged(drawCounter, DrawStatus.OPEN);
            }
        }

        lastDrawDate = today;
    }

    /**
     * @dev Cerrar apuestas para sorteos que estan a 1 hora de comenzar
     */
    function closeExpiredDraws() external onlyAdmin {
        uint256 closeThreshold = block.timestamp + bettingCloseTime;

        for (uint256 i = 1; i <= drawCounter; i++) {
            Draw storage draw = draws[i];

            if (draw.status == DrawStatus.OPEN && draw.scheduledTime <= closeThreshold) {
                draw.status = DrawStatus.CLOSED;
                emit DrawStatusChanged(i, DrawStatus.CLOSED);
            }
        }
    }

    /**
     * @dev Generar resultados aleatorios para sorteos que ya pasaron
     */
    function generateRandomResults(uint256 _drawId) external onlyAdmin drawExists(_drawId) {
        Draw storage draw = draws[_drawId];

        require(draw.status == DrawStatus.CLOSED, "Draw must be closed");
        require(block.timestamp >= draw.scheduledTime, "Draw time not reached");

        // Generar seed usando multiples fuentes de entropia
        randomNonce++;
        uint256 seed = uint256(keccak256(abi.encodePacked(
            block.timestamp,
            block.prevrandao,       // Reemplazo de block.difficulty en PoS
            msg.sender,
            randomNonce,
            draw.totalBets,
            draw.totalAmount,
            blockhash(block.number - 1)
        )));

        // Generar numeros ganadores
        string memory fijos = _generateNumber(seed, 2);
        string memory centenas = _generateNumber(seed >> 64, 3);
        string memory parles = _generateNumber(seed >> 128, 4);

        draw.winningFijos = fijos;
        draw.winningCentenas = centenas;
        draw.winningParles = parles;
        draw.status = DrawStatus.COMPLETED;

        emit RandomResultsGenerated(_drawId, fijos, centenas, parles, seed);
        emit ResultsSubmitted(_drawId, fijos, centenas, parles);
        emit DrawStatusChanged(_drawId, DrawStatus.COMPLETED);
    }

    /**
     * @dev Procesar automaticamente: cerrar y generar resultados para sorteos listos
     */
    function processDraws() external onlyAdmin {
        uint256 closeThreshold = block.timestamp + bettingCloseTime;

        for (uint256 i = 1; i <= drawCounter; i++) {
            Draw storage draw = draws[i];

            // Cerrar sorteos que estan por comenzar
            if (draw.status == DrawStatus.OPEN && draw.scheduledTime <= closeThreshold) {
                draw.status = DrawStatus.CLOSED;
                emit DrawStatusChanged(i, DrawStatus.CLOSED);
            }

            // Generar resultados para sorteos cerrados cuyo tiempo ya paso
            if (draw.status == DrawStatus.CLOSED && block.timestamp >= draw.scheduledTime) {
                randomNonce++;
                uint256 seed = uint256(keccak256(abi.encodePacked(
                    block.timestamp,
                    block.prevrandao,
                    msg.sender,
                    randomNonce,
                    draw.totalBets,
                    draw.totalAmount,
                    i
                )));

                draw.winningFijos = _generateNumber(seed, 2);
                draw.winningCentenas = _generateNumber(seed >> 64, 3);
                draw.winningParles = _generateNumber(seed >> 128, 4);
                draw.status = DrawStatus.COMPLETED;

                emit RandomResultsGenerated(i, draw.winningFijos, draw.winningCentenas, draw.winningParles, seed);
                emit ResultsSubmitted(i, draw.winningFijos, draw.winningCentenas, draw.winningParles);
                emit DrawStatusChanged(i, DrawStatus.COMPLETED);
            }
        }
    }

    /**
     * @dev Configurar tiempo de cierre antes del sorteo
     */
    function setBettingCloseTime(uint256 _seconds) external onlyOwner {
        require(_seconds >= 300 && _seconds <= 7200, "Must be between 5min and 2hours");
        bettingCloseTime = _seconds;
    }

    /**
     * @dev Configurar horarios de sorteos
     */
    function setDrawTimes(uint256 _morning, uint256 _afternoon, uint256 _night) external onlyOwner {
        require(_morning < _afternoon && _afternoon < _night, "Invalid order");
        require(_night < 86400, "Must be within 24 hours");
        drawTimes[0] = _morning;
        drawTimes[1] = _afternoon;
        drawTimes[2] = _night;
    }

    /**
     * @dev Obtener sorteos pendientes de procesar
     */
    function getPendingDraws() external view returns (uint256[] memory) {
        uint256 count = 0;
        for (uint256 i = 1; i <= drawCounter; i++) {
            if (draws[i].status == DrawStatus.OPEN || draws[i].status == DrawStatus.CLOSED) {
                count++;
            }
        }

        uint256[] memory pending = new uint256[](count);
        uint256 index = 0;
        for (uint256 i = 1; i <= drawCounter; i++) {
            if (draws[i].status == DrawStatus.OPEN || draws[i].status == DrawStatus.CLOSED) {
                pending[index] = i;
                index++;
            }
        }
        return pending;
    }

    /**
     * @dev Obtener configuracion de automatizacion
     */
    function getAutomationConfig() external view returns (
        uint256 closeTime,
        uint256[3] memory times,
        string[3] memory names,
        uint256 lastDate
    ) {
        return (bettingCloseTime, drawTimes, drawNames, lastDrawDate);
    }

    // ============ LA FORTUNA - Funciones de Usuario ============

    /**
     * @dev Comprar boleto de loteria La Fortuna
     * @param _lotteryId ID del sorteo
     * @param _numbers Array de 5 numeros (1-54)
     * @param _keyNumber Numero clave (0-9)
     */
    function buyLotteryTicket(
        uint256 _lotteryId,
        uint8[5] calldata _numbers,
        uint8 _keyNumber
    ) external nonReentrant whenNotPaused lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];

        require(lottery.status == LotteryStatus.OPEN, "Lottery not open");
        require(userBalances[msg.sender] >= lotteryTicketPrice, "Insufficient balance");
        require(_keyNumber <= 9, "Key number must be 0-9");

        // Validar numeros (1-54, sin repetir)
        for (uint8 i = 0; i < 5; i++) {
            require(_numbers[i] >= 1 && _numbers[i] <= 54, "Numbers must be 1-54");
            for (uint8 j = i + 1; j < 5; j++) {
                require(_numbers[i] != _numbers[j], "Numbers must be unique");
            }
        }

        // Deducir precio del balance
        userBalances[msg.sender] -= lotteryTicketPrice;

        // Crear boleto
        ticketCounter++;
        lotteryTickets[ticketCounter] = LotteryTicket({
            player: msg.sender,
            lotteryId: _lotteryId,
            numbers: _numbers,
            keyNumber: _keyNumber,
            purchaseTime: block.timestamp,
            claimed: false,
            prize: 0,
            category: 0
        });

        // Actualizar registros
        userLotteryTickets[msg.sender].push(ticketCounter);
        lotteryDrawTickets[_lotteryId].push(ticketCounter);
        lottery.totalTickets++;
        lottery.totalCollected += lotteryTicketPrice;

        // Agregar al jackpot (50% del precio)
        uint256 jackpotContribution = (lotteryTicketPrice * jackpotPercentage) / 10000;
        currentJackpot += jackpotContribution;
        lottery.jackpot = currentJackpot;

        emit LotteryTicketPurchased(ticketCounter, msg.sender, _lotteryId, _numbers, _keyNumber);
        emit JackpotIncreased(currentJackpot, jackpotContribution);
    }

    /**
     * @dev Comprar multiples boletos de loteria (Quick Pick automatico)
     */
    function buyMultipleLotteryTickets(
        uint256 _lotteryId,
        uint256 _quantity
    ) external nonReentrant whenNotPaused lotteryExists(_lotteryId) {
        require(_quantity > 0 && _quantity <= 10, "Buy 1-10 tickets");

        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        require(lottery.status == LotteryStatus.OPEN, "Lottery not open");

        uint256 totalCost = lotteryTicketPrice * _quantity;
        require(userBalances[msg.sender] >= totalCost, "Insufficient balance");

        userBalances[msg.sender] -= totalCost;

        for (uint256 q = 0; q < _quantity; q++) {
            // Generar numeros aleatorios para Quick Pick
            randomNonce++;
            uint256 seed = uint256(keccak256(abi.encodePacked(
                block.timestamp,
                block.prevrandao,
                msg.sender,
                randomNonce,
                q
            )));

            uint8[5] memory numbers;
            uint8 count = 0;

            // Generar 5 numeros unicos del 1-54
            while (count < 5) {
                uint8 num = uint8((seed % 54) + 1);
                seed = seed >> 8;

                bool isDuplicate = false;
                for (uint8 i = 0; i < count; i++) {
                    if (numbers[i] == num) {
                        isDuplicate = true;
                        break;
                    }
                }

                if (!isDuplicate) {
                    numbers[count] = num;
                    count++;
                }

                if (seed == 0) {
                    seed = uint256(keccak256(abi.encodePacked(seed, block.timestamp, randomNonce)));
                }
            }

            // Generar numero clave (0-9)
            uint8 keyNum = uint8(seed % 10);

            // Crear boleto
            ticketCounter++;
            lotteryTickets[ticketCounter] = LotteryTicket({
                player: msg.sender,
                lotteryId: _lotteryId,
                numbers: numbers,
                keyNumber: keyNum,
                purchaseTime: block.timestamp,
                claimed: false,
                prize: 0,
                category: 0
            });

            userLotteryTickets[msg.sender].push(ticketCounter);
            lotteryDrawTickets[_lotteryId].push(ticketCounter);
            lottery.totalTickets++;

            emit LotteryTicketPurchased(ticketCounter, msg.sender, _lotteryId, numbers, keyNum);
        }

        lottery.totalCollected += totalCost;

        // Agregar al jackpot
        uint256 jackpotContribution = (totalCost * jackpotPercentage) / 10000;
        currentJackpot += jackpotContribution;
        lottery.jackpot = currentJackpot;

        emit JackpotIncreased(currentJackpot, jackpotContribution);
    }

    /**
     * @dev Reclamar premio de loteria
     */
    function claimLotteryPrize(uint256 _ticketId) external nonReentrant {
        require(_ticketId > 0 && _ticketId <= ticketCounter, "Ticket does not exist");

        LotteryTicket storage ticket = lotteryTickets[_ticketId];
        require(ticket.player == msg.sender, "Not your ticket");
        require(!ticket.claimed, "Already claimed");

        LotteryDraw storage lottery = lotteryDraws[ticket.lotteryId];
        require(lottery.status == LotteryStatus.COMPLETED, "Lottery not completed");

        // Calcular categoria y premio
        (uint8 category, uint256 prize) = _calculateLotteryPrize(ticket, lottery);

        ticket.claimed = true;
        ticket.category = category;
        ticket.prize = prize;

        if (prize > 0) {
            userBalances[msg.sender] += prize;
            lottery.totalPaidOut += prize;

            if (category == 1) {
                emit JackpotWon(ticket.lotteryId, msg.sender, prize);
            }

            emit LotteryPrizeClaimed(_ticketId, msg.sender, category, prize);
        }
    }

    /**
     * @dev Reclamar todos los premios de loteria pendientes
     */
    function claimAllLotteryPrizes(uint256 _lotteryId) external nonReentrant lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        require(lottery.status == LotteryStatus.COMPLETED, "Lottery not completed");

        uint256[] memory ticketIds = userLotteryTickets[msg.sender];
        uint256 totalPrize = 0;

        for (uint256 i = 0; i < ticketIds.length; i++) {
            LotteryTicket storage ticket = lotteryTickets[ticketIds[i]];

            if (ticket.lotteryId == _lotteryId && !ticket.claimed) {
                (uint8 category, uint256 prize) = _calculateLotteryPrize(ticket, lottery);
                ticket.claimed = true;
                ticket.category = category;
                ticket.prize = prize;
                totalPrize += prize;

                if (category == 1) {
                    emit JackpotWon(_lotteryId, msg.sender, prize);
                }
            }
        }

        if (totalPrize > 0) {
            userBalances[msg.sender] += totalPrize;
            lottery.totalPaidOut += totalPrize;
        }
    }

