            currentJackpot = minJackpot;
        }

        lotteryDraws[lotteryCounter] = LotteryDraw({
            id: lotteryCounter,
            drawName: _drawName,
            scheduledTime: _scheduledTime,
            status: LotteryStatus.OPEN,
            winningNumbers: [uint8(0), uint8(0), uint8(0), uint8(0), uint8(0)],
            keyNumber: 0,
            jackpot: currentJackpot,
            totalTickets: 0,
            totalCollected: 0,
            totalPaidOut: 0,
            jackpotWon: false
        });

        emit LotteryDrawCreated(lotteryCounter, _drawName, _scheduledTime, currentJackpot);
        return lotteryCounter;
    }

    /**
     * @dev Crear sorteos de loteria automaticamente (Miercoles y Sabado)
     */
    function createWeeklyLotteryDraws() external onlyAdmin {
        uint256 today = block.timestamp - (block.timestamp % 86400);
        uint256 dayOfWeek = ((today / 86400) + 4) % 7; // 0=Domingo

        // Crear sorteos para los proximos 7 dias si son Mie o Sab
        for (uint256 i = 0; i < 7; i++) {
            uint256 futureDay = (dayOfWeek + i) % 7;
            uint256 futureTime = today + (i * 86400) + lotteryDrawTime;

            // Solo si es Miercoles (3) o Sabado (6) y en el futuro
            if ((futureDay == 3 || futureDay == 6) && futureTime > block.timestamp) {
                // Verificar que no exista ya un sorteo para esa fecha
                bool exists = false;
                for (uint256 j = 1; j <= lotteryCounter; j++) {
                    if (lotteryDraws[j].scheduledTime == futureTime) {
                        exists = true;
                        break;
                    }
                }

                if (!exists) {
                    lotteryCounter++;

                    if (currentJackpot < minJackpot) {
                        currentJackpot = minJackpot;
                    }

                    string memory dayName = futureDay == 3 ? "MIE" : "SAB";
                    string memory drawName = string(abi.encodePacked(
                        "FORTUNA-",
                        _formatDate(today + (i * 86400)),
                        "-",
                        dayName
                    ));

                    lotteryDraws[lotteryCounter] = LotteryDraw({
                        id: lotteryCounter,
                        drawName: drawName,
                        scheduledTime: futureTime,
                        status: LotteryStatus.OPEN,
                        winningNumbers: [uint8(0), uint8(0), uint8(0), uint8(0), uint8(0)],
                        keyNumber: 0,
                        jackpot: currentJackpot,
                        totalTickets: 0,
                        totalCollected: 0,
                        totalPaidOut: 0,
                        jackpotWon: false
                    });

                    emit LotteryDrawCreated(lotteryCounter, drawName, futureTime, currentJackpot);
                }
            }
        }
    }

    /**
     * @dev Cerrar sorteo de loteria para compras
     */
    function closeLotteryDraw(uint256 _lotteryId) external onlyAdmin lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        require(lottery.status == LotteryStatus.OPEN, "Not open");

        lottery.status = LotteryStatus.CLOSED;
    }

    /**
     * @dev Generar resultados aleatorios para sorteo de loteria
     */
    function generateLotteryResults(uint256 _lotteryId) external onlyAdmin lotteryExists(_lotteryId) {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];

        require(lottery.status == LotteryStatus.CLOSED, "Must be closed first");
        require(block.timestamp >= lottery.scheduledTime, "Draw time not reached");

        // Generar seed
        randomNonce++;
        uint256 seed = uint256(keccak256(abi.encodePacked(
            block.timestamp,
            block.prevrandao,
            msg.sender,
            randomNonce,
            lottery.totalTickets,
            lottery.totalCollected
        )));

        // Generar 5 numeros unicos del 1-54
        uint8[5] memory winningNumbers;
        uint8 count = 0;

        while (count < 5) {
            uint8 num = uint8((seed % 54) + 1);
            seed = seed >> 8;

            bool isDuplicate = false;
            for (uint8 i = 0; i < count; i++) {
                if (winningNumbers[i] == num) {
                    isDuplicate = true;
                    break;
                }
            }

            if (!isDuplicate) {
                winningNumbers[count] = num;
                count++;
            }

            if (seed == 0) {
                seed = uint256(keccak256(abi.encodePacked(seed, block.timestamp, randomNonce)));
            }
        }

        // Generar numero clave (0-9)
        uint8 keyNum = uint8(seed % 10);

        lottery.winningNumbers = winningNumbers;
        lottery.keyNumber = keyNum;
        lottery.status = LotteryStatus.COMPLETED;

        // Verificar si alguien gano el jackpot
        _checkJackpotWinners(_lotteryId);

        emit LotteryResultsGenerated(_lotteryId, winningNumbers, keyNum);
    }

    /**
     * @dev Procesar sorteos de loteria automaticamente
     */
    function processLotteryDraws() external onlyAdmin {
        uint256 closeThreshold = block.timestamp + bettingCloseTime;

        for (uint256 i = 1; i <= lotteryCounter; i++) {
            LotteryDraw storage lottery = lotteryDraws[i];

            // Cerrar sorteos que estan por comenzar
            if (lottery.status == LotteryStatus.OPEN && lottery.scheduledTime <= closeThreshold) {
                lottery.status = LotteryStatus.CLOSED;
            }

            // Generar resultados para sorteos cerrados cuyo tiempo ya paso
            if (lottery.status == LotteryStatus.CLOSED && block.timestamp >= lottery.scheduledTime) {
                randomNonce++;
                uint256 seed = uint256(keccak256(abi.encodePacked(
                    block.timestamp,
                    block.prevrandao,
                    msg.sender,
                    randomNonce,
                    lottery.totalTickets,
                    i
                )));

                uint8[5] memory winningNumbers;
                uint8 count = 0;

                while (count < 5) {
                    uint8 num = uint8((seed % 54) + 1);
                    seed = seed >> 8;

                    bool isDuplicate = false;
                    for (uint8 j = 0; j < count; j++) {
                        if (winningNumbers[j] == num) {
                            isDuplicate = true;
                            break;
                        }
                    }

                    if (!isDuplicate) {
                        winningNumbers[count] = num;
                        count++;
                    }

                    if (seed == 0) {
                        seed = uint256(keccak256(abi.encodePacked(seed, block.timestamp, randomNonce)));
                    }
                }

                lottery.winningNumbers = winningNumbers;
                lottery.keyNumber = uint8(seed % 10);
                lottery.status = LotteryStatus.COMPLETED;

                _checkJackpotWinners(i);

                emit LotteryResultsGenerated(i, lottery.winningNumbers, lottery.keyNumber);
            }
        }
    }

