    // ============ Funciones Internas ============

    function _validateNumbers(BetType _betType, string calldata _numbers) internal pure returns (bool) {
        bytes memory numbersBytes = bytes(_numbers);

        if (_betType == BetType.FIJO && numbersBytes.length != 2) return false;
        if (_betType == BetType.CENTENA && numbersBytes.length != 3) return false;
        if (_betType == BetType.PARLE && numbersBytes.length != 4) return false;

        // Validar que sean solo digitos
        for (uint256 i = 0; i < numbersBytes.length; i++) {
            if (numbersBytes[i] < 0x30 || numbersBytes[i] > 0x39) return false;
        }

        return true;
    }

    /**
     * @dev Generar numero aleatorio de N digitos como string
     */
    function _generateNumber(uint256 _seed, uint256 _digits) internal pure returns (string memory) {
        uint256 num;
        if (_digits == 2) {
            num = _seed % 100;           // 00-99 (100 numeros)
        } else if (_digits == 3) {
            num = (_seed % 900) + 100;   // 100-999 (900 numeros)
        } else {
            num = (_seed % 9000) + 1000; // 1000-9999 (9000 numeros)
        }

        // Convertir a string con padding de ceros
        bytes memory result = new bytes(_digits);
        for (uint256 i = _digits; i > 0; i--) {
            result[i - 1] = bytes1(uint8(48 + (num % 10)));
            num /= 10;
        }

        return string(result);
    }

    /**
     * @dev Formatear timestamp a fecha YYYY-MM-DD
     */
    function _formatDate(uint256 _timestamp) internal pure returns (string memory) {
        // Calcular año, mes, dia desde timestamp
        uint256 z = _timestamp / 86400 + 719468;
        uint256 era = z / 146097;
        uint256 doe = z - era * 146097;
        uint256 yoe = (doe - doe / 1460 + doe / 36524 - doe / 146096) / 365;
        uint256 y = yoe + era * 400;
        uint256 doy = doe - (365 * yoe + yoe / 4 - yoe / 100);
        uint256 mp = (5 * doy + 2) / 153;
        uint256 d = doy - (153 * mp + 2) / 5 + 1;
        uint256 m = mp < 10 ? mp + 3 : mp - 9;

        if (m <= 2) y += 1;

        // Formatear como YYYY-MM-DD
        bytes memory result = new bytes(10);

        // Año (4 digitos)
        result[0] = bytes1(uint8(48 + (y / 1000) % 10));
        result[1] = bytes1(uint8(48 + (y / 100) % 10));
        result[2] = bytes1(uint8(48 + (y / 10) % 10));
        result[3] = bytes1(uint8(48 + y % 10));
        result[4] = "-";

        // Mes (2 digitos)
        result[5] = bytes1(uint8(48 + (m / 10) % 10));
        result[6] = bytes1(uint8(48 + m % 10));
        result[7] = "-";

        // Dia (2 digitos)
        result[8] = bytes1(uint8(48 + (d / 10) % 10));
        result[9] = bytes1(uint8(48 + d % 10));

        return string(result);
    }

    function _calculatePayout(Bet storage bet, Draw storage draw) internal view returns (uint256) {
        bool isWinner = false;
        uint256 multiplier = 0;

        if (bet.betType == BetType.FIJO) {
            isWinner = keccak256(bytes(bet.numbers)) == keccak256(bytes(draw.winningFijos));
            multiplier = fijoMultiplier;
        } else if (bet.betType == BetType.CENTENA) {
            isWinner = keccak256(bytes(bet.numbers)) == keccak256(bytes(draw.winningCentenas));
            multiplier = centenaMultiplier;
        } else if (bet.betType == BetType.PARLE) {
            isWinner = keccak256(bytes(bet.numbers)) == keccak256(bytes(draw.winningParles));
            multiplier = parleMultiplier;
        }

        if (!isWinner) return 0;

        return (bet.amount * multiplier) / 10000;
    }

    /**
     * @dev Registrar relacion de referido
     */
    function _registerReferral(address _user, address _referrer) internal {
        require(referralSystemEnabled, "Referral system disabled");

        referralInfo[_user].referrer = _referrer;
        referralInfo[_user].joinedAt = block.timestamp;

        referralInfo[_referrer].totalReferred++;
        referredUsers[_referrer].push(_user);

        emit ReferralRegistered(_user, _referrer);
    }

    /**
     * @dev Procesar bono de bienvenida en primer deposito
     */
    function _processWelcomeBonus(address _user) internal {
        if (!welcomeBonusEnabled) return;
        if (referralInfo[_user].hasReceivedBonus) return;
        if (welcomeBonusAmount == 0) return;

        // Verificar que el contrato tenga fondos suficientes
        uint256 contractBalance = paymentToken.balanceOf(address(this));
        uint256 totalUserBalances = _getTotalUserBalances();

        if (contractBalance > totalUserBalances + welcomeBonusAmount) {
            referralInfo[_user].hasReceivedBonus = true;
            referralInfo[_user].joinedAt = block.timestamp;
            userBalances[_user] += welcomeBonusAmount;

            emit WelcomeBonusReceived(_user, welcomeBonusAmount);
        }
    }

    /**
     * @dev Procesar bonus al referidor cuando usuario apuesta
     */
    function _processReferralBonus(address _user, uint256 _betAmount) internal {
        if (!referralSystemEnabled) return;

        address referrer = referralInfo[_user].referrer;
        if (referrer == address(0)) return;
        if (referralBonusPercent == 0) return;

        uint256 bonus = (_betAmount * referralBonusPercent) / 10000;
        if (bonus == 0) return;

        // Verificar fondos del contrato
        uint256 contractBalance = paymentToken.balanceOf(address(this));
        uint256 totalUserBalances = _getTotalUserBalances();

        if (contractBalance > totalUserBalances + bonus) {
            referralInfo[referrer].pendingEarnings += bonus;
            referralInfo[referrer].totalEarnings += bonus;

            emit ReferralBonusEarned(referrer, _user, bonus);
        }
    }

    /**
     * @dev Calcular total de balances de usuarios (para verificar fondos)
     */
    function _getTotalUserBalances() internal view returns (uint256) {
        // Nota: En produccion esto deberia ser una variable de estado actualizada
        // Para simplicidad, retornamos un valor conservador
        return paymentToken.balanceOf(address(this)) / 2;
    }

    // ============ Funciones Internas - LA FORTUNA ============

    /**
     * @dev Calcular premio de boleto de loteria
     * Categorias:
     *   1 = 5 aciertos + numero clave = JACKPOT
     *   2 = 5 aciertos = 50,000 USDT
     *   3 = 4 aciertos + numero clave = 5,000 USDT
     *   4 = 4 aciertos = 500 USDT
     *   5 = 3 aciertos + numero clave = 50 USDT
     *   6 = 3 aciertos = 10 USDT
     */
    function _calculateLotteryPrize(
        LotteryTicket storage ticket,
        LotteryDraw storage lottery
    ) internal view returns (uint8 category, uint256 prize) {
        // Contar aciertos
        uint8 matches = 0;
        for (uint8 i = 0; i < 5; i++) {
            for (uint8 j = 0; j < 5; j++) {
                if (ticket.numbers[i] == lottery.winningNumbers[j]) {
                    matches++;
                    break;
                }
            }
        }

        // Verificar numero clave
        bool keyMatch = (ticket.keyNumber == lottery.keyNumber);

        // Determinar categoria y premio
        if (matches == 5 && keyMatch) {
            // Categoria 1: JACKPOT
            return (1, lottery.jackpot);
        } else if (matches == 5) {
            // Categoria 2: 5 aciertos
            return (2, prize2Amount);
        } else if (matches == 4 && keyMatch) {
            // Categoria 3: 4 aciertos + clave
            return (3, prize3Amount);
        } else if (matches == 4) {
            // Categoria 4: 4 aciertos
            return (4, prize4Amount);
        } else if (matches == 3 && keyMatch) {
            // Categoria 5: 3 aciertos + clave
            return (5, prize5Amount);
        } else if (matches == 3) {
            // Categoria 6: 3 aciertos
            return (6, prize6Amount);
        }

        // Sin premio
        return (0, 0);
    }

    /**
     * @dev Verificar si alguien gano el jackpot y resetear si es necesario
     */
    function _checkJackpotWinners(uint256 _lotteryId) internal {
        LotteryDraw storage lottery = lotteryDraws[_lotteryId];
        uint256[] memory ticketIds = lotteryDrawTickets[_lotteryId];

        bool hasJackpotWinner = false;

        for (uint256 i = 0; i < ticketIds.length; i++) {
            LotteryTicket storage ticket = lotteryTickets[ticketIds[i]];

            // Contar aciertos
            uint8 matches = 0;
            for (uint8 j = 0; j < 5; j++) {
                for (uint8 k = 0; k < 5; k++) {
                    if (ticket.numbers[j] == lottery.winningNumbers[k]) {
                        matches++;
                        break;
                    }
                }
            }

            // Si tiene 5 aciertos + clave, gano el jackpot
            if (matches == 5 && ticket.keyNumber == lottery.keyNumber) {
                hasJackpotWinner = true;
                break;
            }
        }

        if (hasJackpotWinner) {
            // Marcar que el jackpot fue ganado y resetear
            lottery.jackpotWon = true;
            currentJackpot = minJackpot; // Resetear al minimo
        }
        // Si no hubo ganador, el jackpot se acumula para el siguiente sorteo
    }

    /**
     * @dev Contar aciertos entre boleto y numeros ganadores
     */
    function _countMatches(uint8[5] memory ticketNumbers, uint8[5] memory winningNumbers) internal pure returns (uint8) {
        uint8 matches = 0;
        for (uint8 i = 0; i < 5; i++) {
            for (uint8 j = 0; j < 5; j++) {
                if (ticketNumbers[i] == winningNumbers[j]) {
                    matches++;
                    break;
                }
            }
        }
        return matches;
    }

    // ============ Funcion de Emergencia ============

    /**
     * @dev Retirar tokens en caso de emergencia (solo owner)
     */
    function emergencyWithdraw(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).safeTransfer(owner(), _amount);
    }
}

